sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    var user = new GlideRecord('sys_user');
    user.get(newValue);
    user.query(response);

    function response(result, answer) {
        while (result.next()) {
            g_form.setValue('department', result.getValue('department'));
            g_form.setValue('title', result.getValue('title'));
            g_form.setValue('company', result.getValue('company'));
        }
    }   
}
